import gql from 'graphql-tag';

const FEATURED_QUERY = gql`
  query Products {
    products(where: {featured: true}) {
      nodes {
        id
        productId
        averageRating
        slug
        name
        shortDescription
        image {
          uri
          title
          srcSet
          sourceUrl
        }
      }
    }
  }
`;

export default FEATURED_QUERY;
